import axios from 'axios';
import React, { useEffect, useState, useRef } from "react";
// import firebase from 'firebase/compat/app';
import { getAuth, signOut } from "firebase/auth";
// Components
import SignInPage from "../components/SignInPage.js";
import { RedirectButton } from '../components/StyleElements.js';


export default function HomePage({token}) {
  let profileButton = <RedirectButton redirectTo="/signin" button={<button type="button" class="btn profile">
      <p> <span>Profile</span> </p>
</button>} />
  if (props.userId !== "") {
        profileButton =
            <button trigger={<div><SignInPage /></div>} closeOnDocumentClick={true}>
                <div><SignInPage /></div>
            </button> 
  }

  useEffect(() => {
    if (token) {
      fetchData(token);
    }
  }, [token]);

  const fetchData = async (token) => {
    console.log("hello")
    const res = await axios.get("http://localhost:8000/api/todos", {
      headers: {
        Authorization: "Bearer " + token,
      }
    });
    console.log(res.data);
  }


  return ( 
    <div style={{backgroundColor:'#dae1e3'}}>
    <div class="titlebar" style="display: flex; justify-content:space-between;">
        <div style={{display: 'flex', justifyContent:'left', paddingLeft:'0.5rem', paddingRight:'0.5rem'}}>
            <h1>Secure U District </h1>
        </div>
        <div style="display: flex; justify-content: flex-end;">
            {profileButton}
        </div>
    </div>
</div>
  );
}
